"use strict";var flag=!0,list2=[];function getList(){$.ajax({url:"../lib/lie.json",dataType:"json",success:function(i){$(".pagi").pagination({pageCount:Math.ceil(i.length/12),current:1,jump:!0,coping:!0,homePage:"首页",endPage:"末页",prevContent:"上页",nextContent:"下页",callback:function(t){var n=t.getCurrent();bindHtml(i.slice(12*(n-1),12*n))}}),bindHtml(i.slice(0,12)),list2=i}})}function bindHtml(t){t.forEach(function(t){str+='\n            <li class="goods1">\n                <img src="'.concat(t.list_url,'">\n                <p>').concat(t.list_name,'</p>\n                <p class="yuan">￥').concat(t.list_price,"</p>\n            </li> \n        ")}),$("#goods").html(str)}getList();var btn=document.querySelector(".sort");btn.onclick=function(){flag=!flag,list2.sort(function(t,n){return!0===flag?t.list_price-n.list_price:n.list_price-t.list_price}),$(".pagi").pagination({pageCount:Math.ceil(list2.length/12),current:1,jump:!0,coping:!0,homePage:"首页",endPage:"末页",prevContent:"上页",nextContent:"下页",callback:function(t){var n=t.getCurrent();bindHtml(list2.slice(12*(n-1),12*n))}}),bindHtml(list2.slice(0,12))};